import csv, os

_data_dir = os.path.join(os.path.dirname(__file__), '..', 'data')

def get_family_and_friends():
    # get family and friends
    with open(os.path.join(_data_dir, "patent_graphs", "family_and_friends.csv"),"r") as faffile:
        reader = csv.reader(faffile)
        return { row[1] : [ int(row[0]), int(row[2]) ] for row in reader }

## nber category/nber subcategory/uspto class/w2v cluster verbose descriptions
def get_category_descriptions_dict():
    return { 1: "Chemical", 2: "Computers & Communications", 3: "Drugs & Medical", 4: "Electrical", 5: "Mechanical", 6: "Other" }

def get_subcategory_descriptions_dict():
    with open(os.path.join(_data_dir, "NBER_categories", "subcategory_descriptions.csv"),"r") as subfile:
        reader = csv.reader(subfile)
        return { i+1 : row[1] for i,row in enumerate(reader) }

def get_class_descriptions():
    with open(os.path.join(_data_dir, "tech_codes", "class_descriptions.csv"),"r") as pfile:
        reader = csv.reader(pfile)
        return { row[0] : row[1] for row in reader }

def get_cluster_descriptions():
    with open(os.path.join(_data_dir, "clusters", "cluster_descriptions.csv"),"r") as cfile:
        reader = csv.reader(cfile)
        return { row[0] : row[1] for row in reader }

